
public class EvenOdd {
	
	void checknum(int a)
	{
		if(a%2==0)
		{
			System.out.println("EVEN");
		}
		else
		{
			System.out.println("ODD");
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		EvenOdd eo = new EvenOdd();
		eo.checknum(43);
		

	}

}
