
public class Ifstmt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=200; int b=200; int c=30;
		if(a>b && a>c)
		{
			System.out.println("a is bigger");
			
		}
		else if(b>c && b>a)
		{
			System.out.println("b is big");
		}
		else
		{
			System.out.println("c is big");
		}
	}
	
	
}
